import React from 'react'
import MovieList from '../../MovieList'
import Carousel from 'react-bootstrap/Carousel'
import { IMG_BASE_URL } from '../../strings'

export default function PopularTab(props) {
    console.log(props)

    // Push amount of items passed in params to list.
    const carouselItems = (amountItems) => {
        const carouselItems = [];
        let i;
        for (i = 0; i < amountItems; i++){
            carouselItems.push(
                <Carousel.Item style={{height: '100%'}}> 
                    <img className="d-block w-100" src={IMG_BASE_URL + props.movies[i].backdrop_path} alt={i + " slide"}/>
                    <Carousel.Caption>
                        <h3>{props.movies[i].original_title}</h3>
                        <p>{props.movies[i].description}</p>
                    </Carousel.Caption>
                </Carousel.Item>
            );
        }
        return carouselItems
    }

    return (
        <>
        {props.movies.length ?
        <Carousel style={{maxHeight: '400px'}}>
            {carouselItems(3)}
        </Carousel> : null}
        <div className="container">
            <h1 className="heading-page">Popular movies</h1>
            {props.movies ?
            <MovieList movies={props.movies}></MovieList>
                : <p>No movies</p>}
        </div>
        </>
    )
}
