import React, { useState, useEffect } from 'react';
import Header from './layout/header';
import './css/App.css';
import MovieList from './components/MovieList';

const API_URL = "https://api.themoviedb.org/3/movie/popular?api_key=da007e76d36aca68e174f2948e09389c&language=en-US&page=1";

const fetchMovies = () => {
  const [movies, setMovies] = useState([]);
    useEffect(async () =>{
      const response  = await fetch(API_URL);
      const data = response.json();
      const item = data.results;
      setMovies(item);
    }, [])

    return movies;
}

const useFetch = url => {
  const [data, setData] = useState(null);

  useEffect(async () => {
    const response = await fetch(url);
    const data = await response.json();
    const [item] = data.results;
    setData(item);
  }, []);

  return { data };
};

export default () =>{
  const { data } = useFetch(API_);

  return (
    <>
      <Header></Header>
      <button onClick={useFetch}>Fetch movies</button>
      {data ?
        <MovieList data={data}></MovieList>
      : console.log(' null')}
    </>
  );
}
