import React, { useEffect, useState } from 'react';
import '../../../css/App.css'
import { IMG_BASE_URL } from '../../MovieDetails'

const LOCAL_STORAGE_KEY = 'movieApp.movies';

export default function MyListTab() {
    const [myMovies, setMyMovies] = useState([])

    useEffect(() =>{
        const storedMovies = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY))
        if (storedMovies) setMyMovies(storedMovies)
    }, [])

    return (
        <div className="container">
            <h1>My List</h1>
            {/** TODO: fix display of my movies here */}
            <table className="table-my-list">
                <tr>
                    <th>#</th>
                    <th>Image</th>
                    <th>Title</th>
                    <th>Rating</th>
                </tr>
                {myMovies.map((movie, index) => {
                    return(
                    <tr>
                        <td style={{color: '#a16575'}}>{index}</td>
                        <td><img src={IMG_BASE_URL + movie.movie.movieItem.poster_path} height={100} /></td>
                        <td>{movie.movie.movieItem.original_title}</td>
                        <td>{movie.movie.movieItem.vote_average}</td>
                    </tr>
                    )
                })}
            </table>
        </div>
    )
}
