import React, { useCallback, useContext } from "react";
import { withRouter, Redirect } from "react-router";
import app from "../../firebase.js";
import { AuthContext } from "../../Auth";
import '../../css/App.css'
import { Container, Row, Col } from 'react-grid-system';
import illustration from '../../images/illustration-cinema.svg';

const Login = ({ history }) => {
  const handleLogin = useCallback(
    async event => {
      event.preventDefault();
      const { email, password } = event.target.elements;
      try {
        await app
          .auth()
          .signInWithEmailAndPassword(email.value, password.value);
        history.push("/");
      } catch (error) {
        alert(error);
      }
    },
    [history]
  );

  const { currentUser } = useContext(AuthContext);

  // If we have current user redirect to root path (home page)
  if (currentUser) {
    return <Redirect to="/" />;
  }

  return (
    <Container fluid="md">
      <Row style={{minHeight: '100vh'}}>
      <Col style={{verticalAlign: 'middle', display: 'table-cell', textAlign: 'center', padding: '4% 0'}}>
        <img src={illustration} height={600}></img>
      </Col>
        <Col className="container login-form">
        <h2>Log in</h2>
        <form onSubmit={handleLogin}>
          <label>
            Email
            <input name="email" type="email" placeholder="Email" />
          </label>
          <label>
            Password
            <input name="password" type="password" placeholder="Password" />
          </label>
          <button type="submit">Log in</button>
        </form>
        </Col>
      </Row>
    </Container>
  );
};

export default withRouter(Login);
