    const data = response.json();
    const item = data.results;
    console.log(item)

    setMovies(item);
  }, []);

  return (
    <>
      <Header></Header>
      {movies ?
        <MovieList movies={movies}></MovieList>
      : <p>No movies</p>}
    </>
  );
}

export default App
