const useFetch = url => {
  const [data, setData] = useState(null);

  useEffect(async () => {
    const response = await fetch(url);
    const data = await response.json();
    let items = []
    for (var i = 0; i < data.results.length; i++) {
      items.push(data.results[i])
    }

    console.log(items)
    setData(items);
  }, []);

  return data;
};

export default () =>{
  const movies = useFetch(API_URL);

  return (
    <>
      <Header></Header>
      <button onClick={useFetch}>Fetch movies</button>
      {movies ?
        <MovieList movies={movies}></MovieList>
      : console.log(' null')}
    </>
  );
}
