import React, {useState, useEffect} from 'react';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import PopularTab from './tabs/PopularTab'
import MyListTab from './tabs/MyListTab'
import app from '../../firebase'
import axios from 'axios'
import { useHistory } from "react-router-dom";
import Carousel from 'react-bootstrap/Carousel'
import { IMG_BASE_URL } from '../strings'

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
        role="tabpanel"
        hidden={value !== index}
        id={`simple-tabpanel-${index}`}
        aria-labelledby={`simple-tab-${index}`}
        {...other}
        >
        {value === index && (
            <Box p={3}>
            <Typography>{children}</Typography>
            </Box>
        )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    };
}

const API_URL = `https://api.themoviedb.org/3/movie/popular?api_key=${process.env.REACT_APP_API_KEY_MOVIEDB}&language=en-US&page=1`;

export default function SimpleTabs() {
    const [value, setValue] = React.useState(0);
    const [movies, setMovies] = useState([]);
    let history = useHistory();

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    useEffect(() => {
        axios.get(API_URL)
        .then(res => {
            console.log(res.data.results)
            setMovies(res.data.results)
        })
        .catch(err => {
            console.log(err)
        })
    }, []);

    const signOut = () => {
        app.auth().signOut();
        history.push("/login");
    }

    // Push amount of items passed in params to list.
    const carouselItems = (amountItems) => {
        const carouselItems = [];
        let i;
        for (i = 0; i < amountItems; i++){
            console.log(movies[i])
            carouselItems.push(
                <Carousel.Item> 
                    <img className="d-block w-100" src={IMG_BASE_URL + movies[i].backdrop_path} alt={i + " slide"}/>
                    <Carousel.Caption>
                        <h3>{movies[i].original_title}</h3>
                        <p>{movies[i].description}</p>
                    </Carousel.Caption>
                </Carousel.Item>
            );
        }
        return carouselItems
    }
    console.lo(movies)

    return (
        <>
        <AppBar position="static">
            <Tabs value={value} onChange={handleChange} aria-label="simple tabs example" className="container">
            <Tab label="Popular" {...a11yProps(0)} />
            <Tab label="My List" {...a11yProps(1)} />
            </Tabs>
        </AppBar>
        <TabPanel value={value} index={0}>
            <PopularTab movies={movies} />
        </TabPanel>
        <TabPanel value={value} index={1}>
            <MyListTab movies={movies} />
        </TabPanel>
        {movies ?
        <Carousel>
            {carouselItems(3)}
        </Carousel> : null}
        <button onClick={signOut} style={{float: 'right'}}>Sign out</button>
        </>
    );
}
