import React, { useEffect, useState, useRef } from 'react';
import '../../../css/App.css'
import uuidv4 from 'uuid/v4';

const LOCAL_STORAGE_KEY = 'movieApp.movies';

export default function MyListTab() {
    const movieNameRef = useRef();
    const [myMovies, setMyMovies] = useState([])

    useEffect(() =>{
        const storedMovies = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY))
        if (storedMovies) setMyMovies(storedMovies)
    }, [])
    
    useEffect(() =>{
        localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(myMovies))
    }, [myMovies])

    const getCurrentDate = () => {
        var currentDate = new Date();
        var dd = String(currentDate.getDate()).padStart(2, '0');
        var mm = String(currentDate.getMonth() + 1).padStart(2, '0'); //January is 0
        var yyyy = currentDate.getFullYear();

        currentDate = mm + '/' + dd + '/' + yyyy;

        return currentDate
    }

    console.log(myMovies)
    return (
        <div className="container">
            <h1>My List</h1>
            <input ref={movieNameRef} type="text"></input>
            <p>{}</p>
        </div>
    )
}
