import React, {useEffect, useState} from 'react'
import {Row, Col} from 'react-grid-system';
import CardDeck from 'react-bootstrap/CardDeck'
import Card from 'react-bootstrap/Card'
import MovieService from '../../services/MovieService'

export default function RecommendedCards(props) {
    const [similarMovies, setSimilarMovies] = useState([]);
    console.log(props.movie)

    /** Get similar movies to the passed movie */
    useEffect(() => {
        MovieService.getSimilarMovies(props.movie.id)
            .then(res => {
                setSimilarMovies(res.data.results);
            })
            .catch(err => {
                alert(`Error getting similar movies: ${err}`);
                console.log(err);
            })
    }, [])

    console.log(similarMovies);
    return (
        <div>
            <br />
            <h2 className="title mt-5">Recommended</h2>
            <Row className="mt-3">
                <CardDeck>
                    <Card bg={'dark'} text={'white'}>
                        <Card.Img variant="top" src="holder.js/100px160" />
                        <Card.Body>
                        <Card.Title>Card title</Card.Title>
                        <Card.Text>
                            This is a wider card with supporting text below as a natural lead-in to
                            additional content. This content is a little bit longer.
                        </Card.Text>
                        </Card.Body>
                        <Card.Footer>
                        <small className="text-muted">Last updated 3 mins ago</small>
                        </Card.Footer>
                    </Card>
                </CardDeck>
            </Row>
        </div>
    )
}
