import axios from 'axios';

const API_GENRES_URL = `https://api.themoviedb.org/3/genre/movie/list?api_key=${process.env.REACT_APP_API_KEY_MOVIEDB}&language=en-US`

class MovieService {

    getGenres(){
        let genres;
        axios.get(API_GENRES_URL).then(res => { genres = res.data.genres }).catch(err => { console.log(err) })
        return genres;
    }
    
}

export default new MovieService();
