import React, { useCallback } from "react";
import { withRouter } from "react-router";
import app from "../../firebase";
import '../../css/App.css'
import { Link } from "react-router-dom"
import { Container, Row, Col } from 'react-grid-system';
import backgroundImg from '../../images/illustration-bg-watch-movies.jpg'

const SignUp = ({ history }) => {
  const handleSignUp = useCallback(async event => {
    event.preventDefault();
    const { email, password } = event.target.elements;
    try {
      await app
        .auth()
        .createUserWithEmailAndPassword(email.value, password.value);
      history.push("/");
    } catch (error) {
      alert(error);
    }
  }, [history]);

  return (
    <Container fluid="md">
      <Row className="login">
      <Col className="illustration" style={{backgroundImage: `url(${backgroundImg})`, backgroundSize: '90%'}}>
      </Col>
        <Col className="container login-form middle">
        <h2>Sign up</h2>
        <form onSubmit={handleSignUp}>
          <label for="email">Email</label>
          <input name="email" type="email" placeholder="Email" required/>

          <label for="password">Password</label>
          <input name="password" type="password" placeholder="Password" required/>

          <button type="submit">Sign up</button>
        </form>
        <p style={{textAlign: 'center'}}>Already have an account? <Link to="/login" className="link">Log in</Link></p>
        </Col>
      </Row>
    </Container>
  );
};

export default withRouter(SignUp);
